-- Whitespace
unicode_nl :: Stream s m Char => ParsecT s u m Char
unicode_nl = oneOf $ ['\x85'] ++ ['\x2028' .. '\x2029']

unicode_space :: Stream s m Char => ParsecT s u m Char
unicode_space = oneOf $ ['\xA0', '\x1680'] ++ ['\x2000' .. '\x200A'] ++ ['\x202F', '\x205F', '\x3000']

